(Scratch => {
  'use strict';

  const icon = 'data:image/svg+xml;base64,';
  const miniNumberBits = 'data:image/svg+xml;base64,';

  const cast = Scratch.Cast;

  const number2bits = number => {
    return Array.from(Array.from('00000000000000000000000000000000' + (number >>> 0).toString(2)).reverse().join('').substring(0, 32)).reverse().join('');
  };
  const bits2number = bits => {
    return parseInt(bits, 2) << 0;
  };

  class Bitwise {

    getInfo() {
      return {

        id: 'Bitwise',
        name: 'Bitwise',

        color1: '#17cde6',

        menuIconURI: icon,

        blocks: [
          {
            opcode: 'isNumberBits',
            blockType: Scratch.BlockType.BOOLEAN,
            text: 'is [IMAGE] [CENTRAL] ?',
            arguments: {
              CENTRAL: {
                type: Scratch.ArgumentType.NUMBER,
                defaultValue: '00000000000000000000000000100000'
              },
              IMAGE: {
                type: Scratch.ArgumentType.IMAGE,
                dataURI: miniNumberBits
              }
            }
          },
          '---',
          {
            opcode: 'toNumberBits',
            blockType: Scratch.BlockType.REPORTER,
            text: '[CENTRAL] to [IMAGE]',
            arguments: {
              CENTRAL: {
                type: Scratch.ArgumentType.NUMBER,
                defaultValue: 32
              },
              IMAGE: {
                type: Scratch.ArgumentType.IMAGE,
                dataURI: miniNumberBits
              }
            }
          },
          {
            opcode: 'ofNumberBits',
            blockType: Scratch.BlockType.REPORTER,
            text: '[CENTRAL] of [IMAGE]',
            arguments: {
              CENTRAL: {
                type: Scratch.ArgumentType.NUMBER,
                defaultValue: '00000000000000000000000000100000'
              },
              IMAGE: {
                type: Scratch.ArgumentType.IMAGE,
                dataURI: miniNumberBits
              }
            }
          },
          '---',
          {
            opcode: 'bitwiseRightShift',
            blockType: Scratch.BlockType.REPORTER,
            text: '[LEFT] ⇉ [RIGHT]',
            arguments: {
              LEFT: {
                type: Scratch.ArgumentType.NUMBER,
                defaultValue: '\n'
              },
              RIGHT: {
                type: Scratch.ArgumentType.NUMBER,
                defaultValue: '\n'
              }
            }
          },
          {
            opcode: 'bitwiseLeftShift',
            blockType: Scratch.BlockType.REPORTER,
            text: '[LEFT] ⇇ [RIGHT]',
            arguments: {
              LEFT: {
                type: Scratch.ArgumentType.NUMBER,
                defaultValue: '\n'
              },
              RIGHT: {
                type: Scratch.ArgumentType.NUMBER,
                defaultValue: '\n'
              }
            }
          },
          {
            opcode: 'bitwiseUnsignedRightShift',
            blockType: Scratch.BlockType.REPORTER,
            text: '[LEFT] ⇶ [RIGHT]',
            arguments: {
              LEFT: {
                type: Scratch.ArgumentType.NUMBER,
                defaultValue: '\n'
              },
              RIGHT: {
                type: Scratch.ArgumentType.NUMBER,
                defaultValue: '\n'
              }
            }
          },
          '---',
          {
            opcode: 'bitwiseAnd',
            blockType: Scratch.BlockType.REPORTER,
            text: '[LEFT] and [RIGHT]',
            arguments: {
              LEFT: {
                type: Scratch.ArgumentType.NUMBER,
                defaultValue: '\n'
              },
              RIGHT: {
                type: Scratch.ArgumentType.NUMBER,
                defaultValue: '\n'
              }
            }
          },
          {
            opcode: 'bitwiseOr',
            blockType: Scratch.BlockType.REPORTER,
            text: '[LEFT] or [RIGHT]',
            arguments: {
              LEFT: {
                type: Scratch.ArgumentType.NUMBER,
                defaultValue: '\n'
              },
              RIGHT: {
                type: Scratch.ArgumentType.NUMBER,
                defaultValue: '\n'
              }
            }
          },
          {
            opcode: 'bitwiseXor',
            blockType: Scratch.BlockType.REPORTER,
            text: '[LEFT] xor [RIGHT]',
            arguments: {
              LEFT: {
                type: Scratch.ArgumentType.NUMBER,
                defaultValue: '\n'
              },
              RIGHT: {
                type: Scratch.ArgumentType.NUMBER,
                defaultValue: '\n'
              }
            }
          },
          {
            opcode: 'bitwiseNot',
            blockType: Scratch.BlockType.REPORTER,
            text: 'not [CENTRAL]',
            arguments: {
              CENTRAL: {
                type: Scratch.ArgumentType.NUMBER,
                defaultValue: '\n'
              }
            }
          }
        ]
      };
    }

    isNumberBits({CENTRAL}) {
      return /^[0-1]{32}$/.test(CENTRAL);
    }
    toNumberBits({CENTRAL}) {
      return number2bits(CENTRAL);
    }
    ofNumberBits({CENTRAL}) {
      return bits2number(CENTRAL);
    }
    bitwiseRightShift({LEFT, RIGHT}) {
      return LEFT >> RIGHT;
    }
    bitwiseLeftShift({LEFT, RIGHT}) {
      return LEFT << RIGHT;
    }
    bitwiseUnsignedRightShift({LEFT, RIGHT}) {
      return LEFT >>> RIGHT;
    }
    bitwiseAnd({LEFT, RIGHT}) {
      return LEFT & RIGHT;
    }
    bitwiseOr({LEFT, RIGHT}) {
      return LEFT | RIGHT;
    }
    bitwiseXor({LEFT, RIGHT}) {
      return LEFT ^ RIGHT;
    }
    bitwiseNot({CENTRAL}) {
      return ~CENTRAL;
    }
  }

  Scratch.extensions.register(new Bitwise());
})(Scratch);
